<script>
export default { 
    name:'WeatherCondition',
    props:{
        code: String,
        size: Number,
        title: String
    },
    methods: {
        getFill: function() {
            switch(this.code) {
                case '01d':
                    return 'yellow';
                case '03d':
                case '04d':
                    return 'white';
                default:
                    if(this.code.charAt(this.code.length-1) === 'n') {
                        return '#8d8dca';
                    }
                    else return 'gray';
            }
        }
    }

}
</script>

